using Microsoft.Office.InfoPath.Server.Controls
protected void XmlFormView1_NotifyHost(object sender, NotifyHostEventArgs e)
{
    TextBox1.Text = e.Notification.ToString();
}

public void CTRL3_5_Clicked(object sender, ClickedEventArgs e)
{
    XPathNavigator myNav = this.MainDataSource.CreateNavigator();
    this.NotifyHost(myNav.SelectSingleNode("/my:myFields/my:field2", this.NamespaceManager).ToString());
}

protected void XmlFormView1_<span class="label">Initialize</span>(object sender, InitializeEventArgs e)
{
    TextBox11.Text = XmlFormView1.EditingStatus.ToString();
}

using Microsoft.Office.InfoPath.Server.Controls
protected void XmlFormView1_<span class="label">NotifyHost</span>(object sender, NotifyHostEventArgs e)
{
    TextBox1.Text = e.Notification.ToString();
}

public void CTRL3_5_Clicked(object sender, ClickedEventArgs e)
{
    XPathNavigator myNav = this.MainDataSource.CreateNavigator();
    this.NotifyHost(myNav.SelectSingleNode("/my:myFields/my:field2", this.NamespaceManager).ToString());
}

protected void XmlFormView1_Initialize(object sender, InitializeEventArgs e)
{
    XmlFormView1.<span class="label">PrintWindow</span> = true;
}

protected void XmlFormView1_<span class="label">SubmitToHost</span>(object sender, SubmitToHostEventArgs e)
{
    TextBox11.Text = e.ReturnStatus.ToString();
}

using System.Xml;
using System.Xml.XPath;
using Microsoft.Office.InfoPath.Server.Controls;
protected void XmlFormView1_NotifyHost(object sender, NotifyHostEventArgs e)
{
    try
    {
        TextBox2.Text = XmlFormView1.<span class="label">XmlForm</span>.New.ToString();
        TextBox3.Text = XmlFormView1.<span class="label">XmlForm</span>.ReadOnly.ToString();
        TextBox4.Text = XmlFormView1.<span class="label">XmlForm</span>.MainDataSource.ReadOnly.ToString();
        TextBox5.Text = XmlFormView1.<span class="label">XmlForm</span>.ToString();
        TextBox6.Text = XmlFormView1.<span class="label">XmlForm</span>.XmlLang.ToString();
        TextBox7.Text = XmlFormView1.<span class="label">XmlForm</span>.Signed.ToString();
        TextBox8.Text = XmlFormView1.<span class="label">XmlForm</span>.FormState.Count.ToString();
        TextBox9.Text = XmlFormView1.<span class="label">XmlForm</span>.DataSources.Count.ToString();
        XPathNavigator xNavMain = XmlFormView1.<span class="label">XmlForm</span>.MainDataSource.CreateNavigator();
        XmlNamespaceManager xNameSpace = new XmlNamespaceManager(new NameTable());
        xNameSpace.AddNamespace("my", XmlFormView1.XmlForm.NamespaceManager.LookupNamespace("my").ToString());
        TextBox10.Text = xNavMain.SelectSingleNode("/my:myFields/my:field2", xNameSpace).ToString();
    }
    catch (Exception ex)
    {
        TextBox11.Text = ex.Message.ToString();
    }
}
